create procedure dbo.USR_USP_DATAFORMTEMPLATE_ADD_BLACKOUTLABELMAILED_BATCH
(
    @ID uniqueidentifier = null output,
	@BATCHID uniqueidentifier,
    @CHANGEAGENTID uniqueidentifier = null,	
	@SEQUENCE int = null,
	@SEQUENCEID int = null,
	@SCANSESSION nvarchar(250) = null,
	@SPONSORLOOKUPID nvarchar(10) = null,
	@ADDRESSEE nvarchar(1500) = null,
	@SALUTATION nvarchar(500) = null,
	@CONTACTADDRESSEE nvarchar(1500) = null,
	@ADDRESS nvarchar(2000) = null,
	@ADDRESS2 nvarchar(2000) = null,
	@CITY nvarchar(2000) = null,
	@STATE nvarchar(500) = null,
	@ZIP nvarchar(200) = null,
	@COUNTRY nvarchar(250) = null,
	@CHILDLOOKUPID nvarchar(50) = null,
	@PROJECTLOOKUPID nvarchar(50) = null
)
as

set nocount on;

if @ID is null
    set @ID = newid()

if @CHANGEAGENTID is null  
    exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output

declare @CURRENTDATE datetime
set @CURRENTDATE = getdate()

begin try
	-- handle inserting the data
	insert into dbo.USR_BLACKOUT_INTERACTIONS_MAILED_BATCH
           ([ID]
           ,[BATCHID]
           ,[SEQUENCE]
           ,[SEQUENCEID]
           ,[SCANSESSION]
           ,[SPONSORLOOKUPID]
           ,[ADDRESSEE]
           ,[SALUTATION]
           ,[CONTACTADDRESSEE]
           ,[ADDRESS]
           ,[ADDRESS2]
           ,[CITY]
           ,[STATE]
           ,[ZIP]
           ,[COUNTRY]
           ,[CHILDLOOKUPID]
           ,[PROJECTLOOKUPID]
           ,[ADDEDBYID]
           ,[CHANGEDBYID]
           ,[DATEADDED]
           ,[DATECHANGED])
     VALUES
		(@ID, 
		@BATCHID,
		@SEQUENCE,
		@SEQUENCEID,
		@SCANSESSION,
		@SPONSORLOOKUPID,
		@ADDRESSEE,
		@SALUTATION,
		@CONTACTADDRESSEE,
		@ADDRESS,
		@ADDRESS2,
		@CITY,
		@STATE,
		@ZIP,
		@COUNTRY,
		@CHILDLOOKUPID,
		@PROJECTLOOKUPID,
		@CHANGEAGENTID, 
		@CHANGEAGENTID, 
		@CURRENTDATE, 
		@CURRENTDATE)
	
end try

begin catch
    exec dbo.USP_RAISE_ERROR
    return 1
end catch

return 0
