GlobalChangeSpec.CreateProcedureSQL:
create procedure dbo.USR_USP_GLOBALCHANGE_CHILD_FULFILLMENTSTATUS ( @IDSETREGISTERID uniqueidentifier = null, @CHANGEAGENTID uniqueidentifier = null, @ASOF as datetime = null, @NUMBERADDED int = 0 output, @NUMBEREDITED int = 0 output, @NUMBERDELETED int = 0 output, @OPERATION int, @ADDFULFILLMENTSTATUSVALUE uniqueidentifier, @OVERWRITEEXISTINGVALUE bit = 0, @REPLACEFULFILLMENTSTATUSVALUE uniqueidentifier, @REPLACEFULFILLMENTSTATUSWITHVALUE uniqueidentifier, @DELETEFULFILLMENTSTATUSVALUE uniqueidentifier, @DELETEALLVALUES bit = 0 ) as set nocount off; --declare @message nvarchar(max) --set @message = @IDSETREGISTERID --raiserror 100000 @message
--if @IDSETREGISTERID is null --raiserror ('IDSETREGISTERID is required',13,1);
declare @CURRENTDATE datetime set @CURRENTDATE = getdate(); set @NUMBERADDED = 0; set @NUMBEREDITED = 0; set @NUMBERDELETED = 0;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output;
begin try -- do the global change operation here, and set @NUMBERADDED, @NUMBEREDITED, and/or @NUMBERDELETED as appropriate
-- perform the Add if @OPERATION = 1 begin -- use the @ADDFULFILLMENTSTATUSVALUE as the new value -- check the @OVERWRITEEXISTINGVALUE flag, if set then just do a big update with the @ADDFULFILLMENTSTATUSVALUE if @OVERWRITEEXISTINGVALUE = 1 begin update dbo.USR_CHILDEXTENSION set FULFILLMENTSTATUSCODEID = @ADDFULFILLMENTSTATUSVALUE, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where SPONSORSHIPOPPORTUNITYCHILDID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) select @NUMBERADDED = @@ROWCOUNT end else begin -- only update where the current value is null update dbo.USR_CHILDEXTENSION set FULFILLMENTSTATUSCODEID = @ADDFULFILLMENTSTATUSVALUE, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where SPONSORSHIPOPPORTUNITYCHILDID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) and FULFILLMENTSTATUSCODEID is null select @NUMBERADDED = @@ROWCOUNT end end
-- perform the replace if @OPERATION = 2 begin update dbo.USR_CHILDEXTENSION set FULFILLMENTSTATUSCODEID = @REPLACEFULFILLMENTSTATUSWITHVALUE, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where SPONSORSHIPOPPORTUNITYCHILDID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) and FULFILLMENTSTATUSCODEID = @REPLACEFULFILLMENTSTATUSVALUE set @NUMBEREDITED = @@ROWCOUNT; end
-- perform the delete if @OPERATION = 3 begin -- check the flag: if not set, only update the matching rows if @DELETEALLVALUES = 0 begin update dbo.USR_CHILDEXTENSION set FULFILLMENTSTATUSCODEID = NULL, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where SPONSORSHIPOPPORTUNITYCHILDID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) and FULFILLMENTSTATUSCODEID = @DELETEFULFILLMENTSTATUSVALUE set @NUMBERDELETED = @@ROWCOUNT; end else -- update all values to NULL begin update dbo.USR_CHILDEXTENSION set FULFILLMENTSTATUSCODEID = NULL, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where SPONSORSHIPOPPORTUNITYCHILDID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) set @NUMBERDELETED = @@ROWCOUNT; end end end try
begin catch exec dbo.USP_RAISE_ERROR; return 1; end catch
