GlobalChangeSpec.CreateProcedureSQL:
create procedure dbo.USR_USP_GLOBALCHANGE_CHILDFIELDMEMO_HOLDREASONCODE_GLOBALCHANGE ( @IDSETREGISTERID uniqueidentifier = null, @CHANGEAGENTID uniqueidentifier = null, @ASOF as datetime = null, @NUMBERADDED int = 0 output, @NUMBEREDITED int = 0 output, @NUMBERDELETED int = 0 output, @OPERATION int, @ADDHOLDREASONCODEVALUE uniqueidentifier = null, @OVERWRITEEXISTINGVALUE bit, @REPLACEHOLDREASONCODEVALUE uniqueidentifier, @REPLACEHOLDREASONCODEWITHVALUE uniqueidentifier, @DELETEHOLDREASONCODEVALUE uniqueidentifier, @DELETEALLHOLDREASONCODEVALUES bit ) as set nocount off;
if @IDSETREGISTERID is null raiserror ('IDSETREGISTERID is required',13,1);
declare @CURRENTDATE datetime set @CURRENTDATE = getdate(); set @NUMBERADDED = 0; set @NUMBEREDITED = 0; set @NUMBERDELETED = 0;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output;
begin try -- do the global change operation here, and set @NUMBERADDED, @NUMBEREDITED, and/or @NUMBERDELETED as appropriate
-- perform the Add if @OPERATION = 1 begin -- use the @ADDHOLDREASONCODEVALUE as the new value -- check the @OVERWRITEEXISTINGVALUE flag, if set then just do a big update with the @ADDHOLDREASONCODEVALUE if @OVERWRITEEXISTINGVALUE = 1 begin update dbo.USR_INTERACTIONEXTENSION set HOLDREASONCODEID = @ADDHOLDREASONCODEVALUE, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where ID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) select @NUMBERADDED = @@ROWCOUNT end else begin -- only update where the current value is null update dbo.USR_INTERACTIONEXTENSION set HOLDREASONCODEID = @ADDHOLDREASONCODEVALUE, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where ID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) and HOLDREASONCODEID is null select @NUMBERADDED = @@ROWCOUNT end end
-- perform the replace if @OPERATION = 2 begin update dbo.USR_INTERACTIONEXTENSION set HOLDREASONCODEID = @REPLACEHOLDREASONCODEWITHVALUE, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where ID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) and HOLDREASONCODEID = @REPLACEHOLDREASONCODEVALUE set @NUMBEREDITED = @@ROWCOUNT; end
-- perform the delete if @OPERATION = 3 begin -- check the flag: if not set, only update the matching rows if @DELETEALLHOLDREASONCODEVALUES = 0 begin update dbo.USR_INTERACTIONEXTENSION set HOLDREASONCODEID = NULL, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where ID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) and HOLDREASONCODEID = @DELETEHOLDREASONCODEVALUE set @NUMBERDELETED = @@ROWCOUNT; end else -- update all values to NULL begin update dbo.USR_INTERACTIONEXTENSION set HOLDREASONCODEID = NULL, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where ID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) set @NUMBERDELETED = @@ROWCOUNT; end end
end try
begin catch exec dbo.USP_RAISE_ERROR; return 1; end catch
