GlobalChangeSpec.CreateProcedureSQL:
create procedure dbo.USR_USP_GLOBALCHANGE_CHILD_INTERACTION_UNUSABLE_ITEM ( @IDSETREGISTERID uniqueidentifier = null, @CHANGEAGENTID uniqueidentifier = null, @ASOF as datetime = null, @NUMBERADDED int = 0 output, @NUMBEREDITED int = 0 output, @NUMBERDELETED int = 0 output, @OPERATION int, @ADD_UNUSABLE_ITEMCODE_VALUE uniqueidentifier = null, @OVERWRITEEXISTINGVALUE bit, @REPLACE_UNUSABLE_ITEMCODE_VALUE uniqueidentifier, @REPLACE_UNUSABLE_ITEMCODE_WITHVALUE uniqueidentifier, @DELETE_UNUSABLE_ITEMCODE_VALUE uniqueidentifier, @DELETEALL_UNUSABLE_ITEMCODE_VALUES bit ) as set nocount off;
declare @CURRENTDATE datetime set @CURRENTDATE = getdate(); set @NUMBERADDED = 0; set @NUMBEREDITED = 0; set @NUMBERDELETED = 0;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output;
begin try -- do the global change operation here, and set @NUMBERADDED, @NUMBEREDITED, and/or @NUMBERDELETED as appropriate
-- perform the Add if @OPERATION = 1 begin -- use the @ADD_UNUSABLE_ITEMCODE_VALUE as the new value -- check the @OVERWRITEEXISTINGVALUE flag, if set then just do a big update with the @ADD_UNUSABLE_ITEMCODE_VALUE if @OVERWRITEEXISTINGVALUE = 1 begin update dbo.USR_INTERACTIONEXTENSION set UNUSABLECODEID = @ADD_UNUSABLE_ITEMCODE_VALUE, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where ID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) select @NUMBERADDED = @@ROWCOUNT end else begin -- only update where the current value is null update dbo.USR_INTERACTIONEXTENSION set UNUSABLECODEID = @ADD_UNUSABLE_ITEMCODE_VALUE, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where ID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) and UNUSABLECODEID is null select @NUMBERADDED = @@ROWCOUNT end end
-- perform the replace if @OPERATION = 2 begin update dbo.USR_INTERACTIONEXTENSION set UNUSABLECODEID = @REPLACE_UNUSABLE_ITEMCODE_WITHVALUE, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where ID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) and UNUSABLECODEID = @REPLACE_UNUSABLE_ITEMCODE_VALUE set @NUMBEREDITED = @@ROWCOUNT; end
-- perform the delete if @OPERATION = 3 begin -- check the flag: if not set, only update the matching rows if @DELETEALL_UNUSABLE_ITEMCODE_VALUES = 0 begin update dbo.USR_INTERACTIONEXTENSION set UNUSABLECODEID = NULL, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where ID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) and UNUSABLECODEID = @DELETE_UNUSABLE_ITEMCODE_VALUE set @NUMBERDELETED = @@ROWCOUNT; end else -- update all values to NULL begin update dbo.USR_INTERACTIONEXTENSION set UNUSABLECODEID = NULL, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where ID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) set @NUMBERDELETED = @@ROWCOUNT; end end
end try
begin catch exec dbo.USP_RAISE_ERROR; return 1; end catch
