GlobalChangeSpec.CreateProcedureSQL:
create procedure dbo.USR_USP_GLOBALCHANGE_PARTNERID ( @IDSETREGISTERID uniqueidentifier = null, @CHANGEAGENTID uniqueidentifier = null, @ASOF as datetime = null, @NUMBERADDED int = 0 output, @NUMBEREDITED int = 0 output, @NUMBERDELETED int = 0 output, @OPERATION int, @ADDVALUE uniqueidentifier = null, @OVERWRITEEXISTINGVALUE bit = 0, @REPLACEVALUE uniqueidentifier = null, @REPLACEWITHVALUE uniqueidentifier = null, @DELETEVALUE uniqueidentifier = null, @DELETEALLVALUES bit = 0 ) as set nocount off;
if @IDSETREGISTERID is null raiserror ('IDSETREGISTERID is required',13,1);
declare @CURRENTDATE datetime set @CURRENTDATE = getdate(); set @NUMBERADDED = 0; set @NUMBEREDITED = 0; set @NUMBERDELETED = 0;
if @CHANGEAGENTID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output;
/* if @OPERATION = 1 RAISERROR ('Add Operation', 16, 1);
if @OPERATION = 2 RAISERROR ('Replace Operation', 16, 1);
if @OPERATION = 3 RAISERROR ('Delete Operation', 16, 1); */
begin try -- do the global change operation here, and set @NUMBERADDED, @NUMBEREDITED, and/or @NUMBERDELETED as appropriate
-- perform the Add if @OPERATION = 1 begin -- use the @ADDVALUE as the new value -- check the @OVERWRITEEXISTINGVALUE flag, if set then just do a big update with the @ADDVALUE if @OVERWRITEEXISTINGVALUE = 1 begin update dbo.USR_INTERACTIONEXTENSION set PARTNERID = @ADDVALUE, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where ID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) select @NUMBERADDED = @@ROWCOUNT end else begin -- only update where the current value is null update dbo.USR_INTERACTIONEXTENSION set PARTNERID = @ADDVALUE, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where ID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) and PARTNERID is null select @NUMBERADDED = @@ROWCOUNT end end
-- perform the replace if @OPERATION = 2 begin update dbo.USR_INTERACTIONEXTENSION set PARTNERID = @REPLACEWITHVALUE, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where ID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) and PARTNERID = @REPLACEVALUE set @NUMBEREDITED = @@ROWCOUNT; end
-- perform the delete if @OPERATION = 3 begin -- check the flag: if not set, only update the matching rows if @DELETEALLVALUES = 0 begin update dbo.USR_INTERACTIONEXTENSION set PARTNERID = NULL, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where ID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) and PARTNERID = @DELETEVALUE set @NUMBERDELETED = @@ROWCOUNT; end else -- update all values to NULL begin update dbo.USR_INTERACTIONEXTENSION set PARTNERID = NULL, DATECHANGED = @CURRENTDATE, CHANGEDBYID = @CHANGEAGENTID where ID in (select ID from dbo.UFN_IDSETREADER_GETRESULTS(@IDSETREGISTERID)) set @NUMBERDELETED = @@ROWCOUNT; end end
end try
begin catch exec dbo.USP_RAISE_ERROR; return 1; end catch
