SPSimpleList.common:CreateProcedureSQL:
create procedure dbo.USR_USP_CODETABLESONLYATTRIBUTECATEGORIESSIMPLEDATALIST ( @RECORDTYPE nvarchar(100) = '', @CURRENTAPPUSERID uniqueidentifier = null ) as set nocount on;
select ATTRIBUTECATEGORY.ID as VALUE, ATTRIBUTECATEGORY.NAME as LABEL from dbo.ATTRIBUTECATEGORY inner join dbo.ATTRIBUTERECORDTYPE on ATTRIBUTERECORDTYPE.ID = ATTRIBUTECATEGORY.ATTRIBUTERECORDTYPEID inner join dbo.RECORDTYPE on RECORDTYPE.ID = ATTRIBUTERECORDTYPE.RECORDTYPEID where RECORDTYPE.NAME = @RECORDTYPE and dbo.UFN_SECURITY_APPUSER_GRANTED_ATTRIBUTECATEGORY_IN_SYSTEMROLE(@CURRENTAPPUSERID,ATTRIBUTECATEGORY.ID) = 1 and ATTRIBUTECATEGORY.DATATYPECODE = 5
order by ATTRIBUTECATEGORY.NAME;
