SQLFunctionSpec.CreateFunctionSQL:
create function dbo.USR_UFN_INTERACTION_GETCATEGORYGROUP ( @categoryGroup nvarchar(50) ) returns @interactioncategories table ( interactionCategoryID uniqueidentifier ) with execute as caller as begin if lower(@categoryGroup) = 'sponsor' insert @interactioncategories select ID as interactionCategoryID from dbo.INTERACTIONCATEGORY where lower(NAME) in ('child change notifications', 'child correspondence', 'misc processing', 'project sponsorship', 'prospect welcome', 'sponsor welcome', 'sponsorship cancellation', 'sponsorship delinquency', 'sponsorship rate increase', 'sponsorship retention', 'sponsorship transfers', 'sympathy and cancellation' )
else if lower(@categoryGroup) = 'child' insert @interactioncategories select ID as interactionCategoryID from dbo.INTERACTIONCATEGORY where lower(NAME) in ('child field memo', 'child letters')
return end
