SPDataForm.c:CreateProcedureSQL:
create procedure dbo.USP_DATAFORMTEMPLATE_VIEW_INTERACTION_EXTENSION 
(
	@ID uniqueidentifier,
	@DATALOADED bit = 0 output,
    @LETTERTYPECODE nvarchar(100) = null output,
    @CHILDCOUNTLETTERVERSIONCODE nvarchar(100) = null output,
    @FULFILLMENTSTATUSCODE nvarchar(100) = null output,
    @EFTBROCHURECODE tinyint = null output,
    @RESENDCODE tinyint = null output,
    @UNAVAILABLECHILDID uniqueidentifier = null output,
    @TRANSFERCHILDID uniqueidentifier = null output,
    @DEPARTEDCHILDID uniqueidentifier = null output,
    @DEPARTUREREASONCODE nvarchar(100) = null output,
    @DOUBLESPONSOREDCHILDID uniqueidentifier = null output,
    @PREVIOUSCHILDPROJECTID uniqueidentifier = null output,
    @PREVIOUSBIRTHDATE datetime = null output,
    @PREVIOUSNAME nvarchar(150) = null output,
    @FIELDMEMODATESENT datetime = null output,
    @FIELDMEMOSENTCODE nvarchar(100) = null output,
    @HOLDREASONCODE nvarchar(100) = null output,
    @UNUSABLECODE nvarchar(100) = null output
	--@CHILDREN xml = null output
)
as
	set nocount on;
	
	-- be sure to set this, in case the select returns no rows
	set @DATALOADED = 0;
	
	-- populate the output parameters, which correspond to fields on the form.  Note that
	-- we set @DATALOADED = 1 to indicate that the load was successful.  Otherwise, the system
	-- will display a "no data loaded" message.
	select @DATALOADED = 1,
           @LETTERTYPECODE = (select DESCRIPTION from dbo.USR_INTERACTIONEXTENSIONLETTERTYPECODE where ID = LETTERTYPECODEID), 
           @CHILDCOUNTLETTERVERSIONCODE = (select DESCRIPTION from dbo.USR_INTERACTIONEXTENSIONLETTERCHILDVERSIONCODE where ID = CHILDCOUNTLETTERVERSIONCODEID), 
           @FULFILLMENTSTATUSCODE = (select DESCRIPTION from dbo.USR_INTERACTIONEXTENSIONFULFILLMENTSTATUSCODE where ID = FULFILLMENTSTATUSCODEID), 
           @EFTBROCHURECODE = EFTBROCHURECODE, 
           @RESENDCODE = RESENDCODE, 
           @UNAVAILABLECHILDID = UNAVAILABLECHILDID, 
           @TRANSFERCHILDID = TRANSFERCHILDID, 
           @DEPARTEDCHILDID = DEPARTEDCHILDID, 
           @DEPARTUREREASONCODE = (select DESCRIPTION from dbo.USR_INTERACTIONEXTENSIONDEPARTUREREASONCODE where ID = DEPARTUREREASONCODEID), 
           @DOUBLESPONSOREDCHILDID = DOUBLESPONSOREDCHILDID, 
           @PREVIOUSCHILDPROJECTID = PREVIOUSCHILDPROJECTID, 
           @PREVIOUSBIRTHDATE = PREVIOUSBIRTHDATE, 
           @PREVIOUSNAME = PREVIOUSNAME, 
           @FIELDMEMODATESENT = FIELDMEMODATESENT, 
           @FIELDMEMOSENTCODE = (select DESCRIPTION from dbo.USR_INTERACTIONEXTENSIONFIELDMEMOSENTCODE where ID = FIELDMEMOSENTCODEID), 
           @HOLDREASONCODE = (select DESCRIPTION from dbo.USR_INTERACTIONEXTENSIONHOLDREASONCODE where ID = HOLDREASONCODEID), 
           @UNUSABLECODE = (select DESCRIPTION from dbo.USR_INTERACTIONEXTENSIONUNUSABLEITEMCODE where ID = UNUSABLECODEID)
--		   @CHILDREN = dbo.USR_UFN_INTERACTIONEXTENSION_GETCHILDREN_TOITEMLISTXML(@ID)
	from dbo.USR_INTERACTIONEXTENSION
	where ID = @ID
		
	return 0;
