LoadImplementation.common:CreateProcedureSQL:
				
create procedure dbo.USP_DATAFORMTEMPLATE_EDITLOAD_INTERACTIONEXTENSION_UIMODELFIELDMANAGER_CONFIG(
    @ID uniqueidentifier,
    @DATALOADED bit = 0 output,
    @TSLONG bigint = 0 output,
	@UIMODELFIELDCODEID uniqueidentifier = null output,
	@ISVISIBLE bit = null output,
	@ISREQUIRED bit = null output
)
as

	set nocount on;

	-- be sure to set these, in case the select returns no rows
	set @DATALOADED = 0
	set @TSLONG = 0

	-- populate the output parameters, which correspond to fields on the form.  Note that
	-- we set @DATALOADED = 1 to indicate that the load was successful.  Otherwise, the system
	-- will display a "no data loaded" message.  Also note that we fetch the TSLONG so that concurrency
	-- can be considered.
	select	@DATALOADED = 1,
			@TSLONG = TSLONG,
			@UIMODELFIELDCODEID = UIMODELFIELDCODEID,
			@ISVISIBLE = ISVISIBLE,
			@ISREQUIRED = ISREQUIRED		
	  from	dbo.USR_INTERACTIONEXTENSION_UIMODELFIELDMANAGER
	 where	ID = @ID

	return 0;

SaveImplementation.common:CreateProcedureSQL:
create procedure dbo.USP_DATAFORMTEMPLATE_EDIT_INTERACTIONEXTENSION_UIMODELFIELDMANAGER_CONFIG ( @ID uniqueidentifier, @CHANGEAGENTID uniqueidentifier = null, @UIMODELFIELDCODEID uniqueidentifier, @ISVISIBLE bit, @ISREQUIRED bit ) as
set nocount on;
if @changeAgentID is null exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @changeAgentID output
declare @currentDate datetime set @currentDate = getdate()
begin try -- handle updating the data update dbo.USR_INTERACTIONEXTENSION_UIMODELFIELDMANAGER set UIMODELFIELDCODEID = @UIMODELFIELDCODEID, ISVISIBLE = @ISVISIBLE, ISREQUIRED = @ISREQUIRED, CHANGEDBYID = @CHANGEAGENTID, DATECHANGED = @CURRENTDATE where ID = @ID end try begin catch exec dbo.USP_RAISE_ERROR return 1 end catch
return 0;
