SPDataList.common:CreateProcedureSQL:
create procedure dbo.USR_USP_DATALIST_LETTER_SCANNER_LOG ( @LOGDATE date = null ) as set nocount on;
if @LOGDATE is null set @LOGDATE = cast(getdate() as date);
select isc.NAME as LETTERFULLNAME, lsl.SCANOUTCOME, lsl.[DESCRIPTION], lsl.LOGTYPE, c.NAME as SPONSOR, sc.NAME as CHILD, sl.NAME as CHILDPROJECT, au.USERNAME, lsl.SCANSESSION, lsl.SEQUENCEID, lsl.ORIGINALSEQUENCEID, lsl.CANCELLATIONREASON, lsl.DATESPONSORSHIPCANCELLED, lsl.NUMBEROFDAYSSPONSORED from dbo.USR_LETTERSCANNERLOG lsl left join dbo.SPONSORSHIPOPPORTUNITYCHILD sc on lsl.childid = sc.ID left join dbo.SPONSORSHIPOPPORTUNITY so on so.ID = sc.ID left join dbo.CONSTITUENT c on c.ID = lsl.sponsorid left join dbo.CHANGEAGENT au on lsl.addedbyid = au.ID left join dbo.SPONSORSHIPLOCATION sl on sl.ID = so.SPONSORSHIPLOCATIONID left join dbo.INTERACTIONSUBCATEGORY isc on lsl.LETTERSUBCATEGORYID = isc.ID where cast(lsl.DATEADDED as date) = @LOGDATE order by lsl.DATEADDED desc --where CONTEXTID = @CONTEXTID
