ViewImplementation.ViewSQL:
with RGInstallmentsWithBalance as (select ft.ID as REVENUEID, i.ID as INSTALLMENTID, ft.CONSTITUENTID, i.TRANSACTIONAMOUNT as INSTALLMENTAMOUNT, dbo.UFN_RECURRINGGIFTINSTALLMENT_GETINSTALLMENTBALANCE(i.ID) as INSTALLMENTBALANCE, rs.NEXTTRANSACTIONDATE, ft.CALCULATEDUSERDEFINEDID as REVENUELOOKUPID from dbo.RECURRINGGIFTINSTALLMENT i join dbo.FINANCIALTRANSACTION ft ON ft.ID = i.REVENUEID join dbo.REVENUESCHEDULE rs ON rs.ID = ft.ID join dbo.CONSTITUENT c on ft.CONSTITUENTID = c.ID where i.[DATE] = rs.NEXTTRANSACTIONDATE and (rs.STATUSCODE = 0 or rs.STATUSCODE = 1) -- active and held
) select ib1.CONSTITUENTID, ib1.REVENUEID, ib1.INSTALLMENTID, c.LOOKUPID as CONSTITUENTLOOKUPID, c.NAME as CONSTITUENTNAME, ib1.INSTALLMENTAMOUNT, ib1.INSTALLMENTBALANCE, ib1.NEXTTRANSACTIONDATE, ib1.REVENUELOOKUPID from RGInstallmentsWithBalance ib1 join dbo.CONSTITUENT c on c.ID = ib1.CONSTITUENTID
where INSTALLMENTBALANCE = INSTALLMENTAMOUNT and exists (SELECT INSTALLMENTID FROM RGInstallmentsWithBalance ib2 where ib2.REVENUEID = ib1.REVENUEID and ib2.INSTALLMENTID <> ib1.INSTALLMENTID and ib2.INSTALLMENTBALANCE > 0 and ib2.INSTALLMENTBALANCE < ib2.INSTALLMENTAMOUNT)
