LoadImplementation.common:CreateProcedureSQL:

create procedure dbo.USR_USP_LOAD_RUNSQL_UTILITY(
	@ID uniqueidentifier,
	@DATALOADED bit = 0 output,
	@COMMAND nvarchar(max) = null output,
	@RESULTS nvarchar(max) = null output	
)
as

	set nocount on;

	set @DATALOADED = 0;
	declare @name nvarchar(max);

	begin try		
		select @DATALOADED = 1,
			   @name = [NAME],
			   @COMMAND = Value,
			   @RESULTS = ''
		from dbo.Setting
		where ID = 1
	end try
	begin catch
		exec dbo.USP_RAISE_ERROR
		return 1
	end catch

return 0;

SaveImplementation.common:CreateProcedureSQL:

create procedure dbo.USR_USP_DATAFORMTEMPLATE_EDIT_RUNSQL_UTILITY(
	@ID uniqueidentifier,
	@CHANGEAGENTID uniqueidentifier = null,
	@COMMAND nvarchar(max),
	@RESULTS nvarchar(max)
)
as

	set nocount on;

	if @CHANGEAGENTID is null  
		exec dbo.USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output

	declare @CURRENTDATE datetime
	set @CURRENTDATE = getdate()

	begin try
		declare @foobar table (ID uniqueidentifier)

		insert into @foobar
		select newid()
	end try
	begin catch
		exec dbo.USP_RAISE_ERROR
		return 1
	end catch

return 0;
